mapper.select:
select count(*)
from
orderlist where member_id = #{member_id} and product_id = #{product_id}

select * from orderlist

		AND order_id = #{order_id}
		AND member_id like '${member_id}'
		AND and product_id = #{product_id}
		AND pay_date = '${pay_date}'
		AND rent_start='${rent_start}'
		AND rent_end='${rent_end}'
		AND rent_date=#{rent_date}

order by order_id

select pay_date,order_id,main_img,rent_month,product_name,price,condition con,p.product_id,p.price,p.category,p.quality,o.member_id,rent_start,rent_end,orderlist_condition from product p,orderlist o where p.product_id=o.product_id and o.member_id = #{member_id}
	AND product_name= '${product_name}'
	AND category= '${category}'
	AND price = ${price}
	AND orderlist_condition = '${condition}'
order by o.pay_date desc

mapper.update:
update orderlist set rent_month = ${rent_month}
where
order_id = ${order_id}

update orderlist set
member_id = #{member_id}, product_id = #{product_id}, pay_date = #{pay_date}, rent_start=#{rent_start}, rent_end= #{rent_end}, rent_month= #{rent_month}, orderlist_condition= #{orderlist_condition},
where order_id=#{order_id}

mapper.delete:
delete orderlist
where member_id = #{member_id} and
product_id = #{product_id}
