mapper.update:
update board set
title=#{title},writer=#{writer},content=#{content},fname=#{fname},fszie=#{fszie}
where no=#{no} and pwd=#{pwd}

mapper.select:
select no, title,writer,pwd,content,regdate,ip,fname,b_ref,b_level,b_step,fszie,hit from
(select rownum n, no, title,writer,pwd,content,regdate,ip,fname,b_ref,b_level,b_step,fszie,hit from
(select * from board

	where writer = #{writer}

order by b_ref desc, b_step)) a where a.n between #{start} and #{end}
